mapper.select:
    
	SELECT A.CAL_YMD
		  ,A.INOUT_SEQ_NO
		  ,A.USER_KEY
		  ,A.INOUT_CD
		  ,A.INOUT_ITEM_CD
		  ,A.INOUT_AMT
		  ,A.REMARK
	FROM WEEK_INOUT A
	    ,MEMBER B
	WHERE CAL_YMD = #{CAL_YMD}
	AND INOUT_CD = #{INOUT_CD}
	AND A.USER_KEY = B.USER_KEY
			
	
	AND B.USER_NM LIKE CONCAT('%', #{USER_NM} , '%')
		
	
	AND A.INOUT_ITEM_CD = #{INOUT_ITEM_CD}
						
	ORDER BY ${sidx} ${sord} 
	LIMIT ${page}, ${rows}

    
	SELECT COUNT(*) ROWCOUNT
	FROM WEEK_INOUT A
	    ,MEMBER B
	WHERE CAL_YMD = #{CAL_YMD}
	AND INOUT_CD = #{INOUT_CD}
	AND A.USER_KEY = B.USER_KEY
		
	
	AND B.USER_NM LIKE CONCAT('%', #{USER_NM} , '%')
		
	
	AND A.INOUT_ITEM_CD = #{INOUT_ITEM_CD}

    
	SELECT IFNULL(MAX(INOUT_SEQ_NO)+1,1) SEQ	      
	FROM WEEK_INOUT
	WHERE CAL_YMD = #{CAL_YMD}

mapper.insert:
    
		INSERT INTO WEEK_INOUT 
		(  
			 CAL_YMD
			,INOUT_SEQ_NO
			,USER_KEY
			,INOUT_CD
			,INOUT_ITEM_CD
			,INOUT_AMT
			,REMARK
			,UPD_ID
			,UPD_DATE
			,CRE_ID
			,CRE_DATE
		)VALUES(
			 #{CAL_YMD}
			,#{INOUT_SEQ_NO}
			,#{USER_KEY, jdbcType=VARCHAR}
			,#{INOUT_CD}
			,#{INOUT_ITEM_CD}
			,#{INOUT_AMT}
			,#{REMARK, jdbcType=VARCHAR}
			,#{UPD_ID}
			,NOW()
			,#{CRE_ID}
			,NOW()
		)

mapper.update:
    
		UPDATE WEEK_INOUT
		SET  INOUT_AMT	= #{INOUT_AMT}
			,REMARK	= #{REMARK, jdbcType=VARCHAR}
			,UPD_ID		= #{UPD_ID}
			,UPD_DATE	= NOW()				
		WHERE CAL_YMD = #{CAL_YMD}
		AND   USER_KEY = #{USER_KEY}
		AND   INOUT_CD = #{INOUT_CD}
		AND   INOUT_ITEM_CD	= #{INOUT_ITEM_CD}
		AND   INOUT_AMT != #{INOUT_AMT}

mapper.select:
    
		SELECT CAL_YMD
			  ,INOUT_SEQ_NO
			  ,USER_KEY
			  ,INOUT_CD
			  ,INOUT_ITEM_CD
			  ,INOUT_AMT
			  ,REMARK
		FROM WEEK_INOUT
		WHERE CAL_YMD = #{CAL_YMD}
		AND   USER_KEY = #{USER_KEY}
		AND   INOUT_CD = #{INOUT_CD}
		AND   INOUT_ITEM_CD	= #{INOUT_ITEM_CD}
				
	
	AND INOUT_SEQ_NO = #{INOUT_SEQ_NO}

    
	SELECT A.INOUT_ITEM_CD
	      ,SUM(A.INOUT_AMT ) INOUT_AMT
	FROM WEEK_INOUT A,
		  MEMBER B 
	WHERE A.CAL_YMD = #{CAL_YMD} 
	AND A.INOUT_CD = #{INOUT_CD} 
	AND A.USER_KEY = B.USER_KEY
	AND A.INOUT_AMT > 0
	
	
	AND A.INOUT_ITEM_CD = #{INOUT_ITEM_CD}
							
	GROUP BY A.INOUT_ITEM_CD

    
	SELECT CAL_YMD
		  ,INOUT_SEQ_NO
		  ,USER_KEY
		  ,INOUT_CD
		  ,INOUT_ITEM_CD
		  ,INOUT_AMT
		  ,REMARK
	FROM WEEK_INOUT
	WHERE CAL_YMD = #{CAL_YMD}
	AND INOUT_CD = #{INOUT_CD}
	AND USER_KEY IS NULL
			
	ORDER BY ${sidx} ${sord} 
	LIMIT ${page}, ${rows}

    
	SELECT   COUNT(*) ROWCOUNT
	FROM WEEK_INOUT
	WHERE CAL_YMD = #{CAL_YMD}
	AND INOUT_CD = #{INOUT_CD}
	AND USER_KEY IS NULL

mapper.update:
    
		UPDATE WEEK_INOUT
		SET  INOUT_AMT	= #{INOUT_AMT}
			,REMARK	= #{REMARK}
			,UPD_ID		= #{UPD_ID}
			,UPD_DATE	= NOW()				
		WHERE CAL_YMD = #{CAL_YMD}
		AND   INOUT_SEQ_NO = #{INOUT_SEQ_NO}

mapper.delete:
    
	DELETE FROM WEEK_INOUT
	WHERE CAL_YMD = #{CAL_YMD}
	AND   INOUT_SEQ_NO = #{INOUT_SEQ_NO}
