mapper.select:
SELECT DATE_FORMAT(CAL_YMD, '%Y-%m-%d') CAL_YMD ,PREV_AMT ,IN_AMT ,OUT_AMT ,NEXT_AMT ,WS_NM ,END_YN FROM WEEK_SUM WHERE 1 = 1
AND CAL_YMD = #{CAL_YMD}
AND CAL_YMD LIKE CONCAT(#{YEAR}, '%')
AND SUBSTR(CAL_YMD, 5) LIKE CONCAT(#{MON}, '%')
ORDER BY ${sidx} ${sord} LIMIT ${page}, ${rows}

    
	SELECT SUM(A.INOUT_AMT) INOUT_AMT
	FROM WEEK_INOUT A
	WHERE A.CAL_YMD > (SELECT IFNULL(MAX(CAL_YMD), DATE_ADD(#{CAL_YMD}, INTERVAL -1 DAY))
					   FROM WEEK_SUM
					   WHERE CAL_YMD < #{CAL_YMD}
					   ) 
	AND A.CAL_YMD <= #{CAL_YMD}
	AND A.INOUT_CD = #{INOUT_CD}
	AND A.INOUT_AMT > 0

mapper.update:
    
    UPDATE WEEK_SUM
    SET END_YN = #{END_YN}
       ,UPD_ID = #{UPD_ID}
       ,UPD_DATE = NOW()
    WHERE CAL_YMD = #{CAL_YMD}

mapper.insert:
    
    INSERT INTO WEEK_SUM_DETL
    (CAL_YMD, DETL_SEQ_NO, INOUT_CAL_YMD, INOUT_SEQ_NO, UPD_ID, UPD_DATE, CRE_ID, CRE_DATE)	
	SELECT #{CAL_YMD}
		  ,(IFNULL((SELECT MAX(DETL_SEQ_NO)+1 FROM WEEK_SUM_DETL WHERE CAL_YMD = #{CAL_YMD}),1) + (@RNUM := @RNUM + 1))
	      ,A.CAL_YMD
		  ,A.INOUT_SEQ_NO
		  ,#{UPD_ID}
		  ,NOW()
		  ,#{CRE_ID}
		  ,NOW()
	FROM WEEK_INOUT A
	    ,( SELECT @RNUM := 0 ) R
	WHERE A.CAL_YMD > (SELECT IFNULL(MAX(CAL_YMD), DATE_ADD(#{CAL_YMD}, INTERVAL -1 DAY))
					   FROM WEEK_SUM
					   WHERE CAL_YMD < #{CAL_YMD}
					   ) 
	AND A.CAL_YMD <= #{CAL_YMD}
	AND A.INOUT_AMT > 0

mapper.select:
SELECT DATE_FORMAT(CONCAT(CAL_YM,'01'), '%Y-%m') CAL_YM ,PREV_AMT ,IN_AMT ,OUT_AMT ,NEXT_AMT ,END_YN FROM MON_SUM WHERE 1 = 1
AND CAL_YM = #{CAL_YM}
AND CAL_YM LIKE CONCAT(#{YEAR}, '%')
ORDER BY ${sidx} ${sord} LIMIT ${page}, ${rows}

SELECT COUNT(*) ROWCOUNT FROM MON_SUM WHERE 1 = 1
AND CAL_YM = #{CAL_YM}
AND CAL_YM LIKE CONCAT(#{YEAR}, '%')

mapper.update:
    
    UPDATE MON_SUM
    SET END_YN = #{END_YN}
       ,UPD_ID = #{UPD_ID}
       ,UPD_DATE = NOW()
    WHERE CAL_YM = #{CAL_YM}

    
    UPDATE YEAR_SUM
    SET END_YN = #{END_YN}
       ,UPD_ID = #{UPD_ID}
       ,UPD_DATE = NOW()
    WHERE CAL_YEAR = #{YEAR}
