mapper.select:
    
	SELECT SUM(A.INOUT_AMT) INOUT_AMT
	FROM WEEK_INOUT A
	WHERE A.CAL_YMD > (SELECT IFNULL(MAX(CAL_YMD), DATE_ADD(#{CAL_YMD}, INTERVAL -1 DAY))
					   FROM WEEK_SUM
					   WHERE CAL_YMD < #{CAL_YMD}
					   ) 
	AND A.CAL_YMD <= #{CAL_YMD}
	AND A.INOUT_CD = #{INOUT_CD}
	AND A.INOUT_AMT > 0

mapper.update:
    
    UPDATE WEEK_SUM
    SET END_YN = #{END_YN}
       ,UPD_ID = #{UPD_ID}
       ,UPD_DATE = NOW()
    WHERE CAL_YMD = #{CAL_YMD}

mapper.insert:
    
    INSERT INTO WEEK_SUM_DETL
    (CAL_YMD, DETL_SEQ_NO, INOUT_CAL_YMD, INOUT_SEQ_NO, UPD_ID, UPD_DATE, CRE_ID, CRE_DATE)	
	SELECT #{CAL_YMD}
		  ,(SELECT IFNULL(MAX(DETL_SEQ_NO)+1,1) FROM WEEK_SUM_DETL WHERE CAL_YMD = #{CAL_YMD})
	      ,A.CAL_YMD
		  ,A.INOUT_SEQ_NO
		  ,#{UPD_ID}
		  ,NOW()
		  ,#{CRE_ID}
		  ,NOW()
	FROM WEEK_INOUT A
	WHERE A.CAL_YMD > (SELECT IFNULL(MAX(CAL_YMD), DATE_ADD(#{CAL_YMD}, INTERVAL -1 DAY))
					   FROM WEEK_SUM
					   WHERE CAL_YMD < #{CAL_YMD}
					   ) 
	AND A.CAL_YMD <= #{CAL_YMD}
	AND A.INOUT_AMT > 0
