mapper.insert:
INSERT INTO CODE (
        CD_VAL
    ,   HICD_VAL
    ,   CD_NM
    ,   ORD_NO
    ,   USE_YN
    ,   ETC1_VAL
    ,   ETC2_VAL
) VALUES
(
        #{CD_VAL}
    ,   #{HICD_VAL}
    ,   #{CD_NM}
    ,   #{ORD_NO,jdbcType=VARCHAR}
    ,   #{USE_YN,jdbcType=VARCHAR}
    ,   #{ETC1_VAL,jdbcType=VARCHAR}
    ,   #{ETC2_VAL,jdbcType=VARCHAR}
    		    
)

mapper.update:
UPDATE  CODE
SET     CD_NM            =   #{CD_NM}
    ,   ORD_NO           =   #{ORD_NO}
    ,   USE_YN           =   #{USE_YN}
    ,   ETC1_VAL         =   #{ETC1_VAL}
    ,   ETC2_VAL         =   #{ETC2_VAL}
WHERE   HICD_VAL         =   #{HICD_VAL}
AND     CD_VAL           =   #{CD_VAL}

mapper.delete:
UPDATE  CODE
SET     USE_YN      =   #{USE_YN}
WHERE   HICD_VAL         =   #{HICD_VAL}
AND     CD_VAL           =   #{CD_VAL}
